import logo from "./logo.svg";
import "./App.css";
import { Box, Container, Divider } from "@mui/material";
import { sizing } from "@mui/system";
import SensorValueRow from "./components/SensorValueRow";
import { useState } from "react";

function App() {
  const distanceMeasure = "mm";
  const temperatureMeasure = "Celcius";
  const [state, setState] = useState({
    mtS1: 0,
    mtS2: 0,
    tmpS: 0,
  });

  return (
    <div className="App-header">
      <Container
        maxWidth="lg"
        sx={{ backgroundColor: "#778899", minHeight: "100vh" }}
      >
        <h2>Sensor Data:</h2>
        <div style={{ display: "flex", flexDirection: "row" }}>
          <div style={{ flex: 1 }} />
          <div style={{ flex: 15, flexDirection: "column" }}>
            <SensorValueRow
              description={"Motion sensor (1)"}
              value={state.mtS1}
              measure={distanceMeasure}
            />
            <SensorValueRow
              description={"Motion sensor (2)"}
              value={state.mtS2}
              measure={distanceMeasure}
            />
            <SensorValueRow
              description={"Temperature sensor"}
              value={state.tmpS}
              measure={temperatureMeasure}
            />
          </div>
        </div>

        <Divider/>

        <h2>Device Status:</h2>
        <div style={{ display: "flex", flexDirection: "row" }}>
          <div style={{ flex: 1 }} />
          <div style={{ flex: 15, flexDirection: "column" }}>
            <SensorValueRow
              description={"Motion sensor (1)"}
              value={state.mtS1}
              measure={distanceMeasure}
            />
            <SensorValueRow
              description={"Motion sensor (2)"}
              value={state.mtS2}
              measure={distanceMeasure}
            />
            <SensorValueRow
              description={"Temperature sensor"}
              value={state.tmpS}
              measure={temperatureMeasure}
            />
          </div>
        </div>

      </Container>
    </div>
  );
}

export default App;
