import { ToggleButton, ToggleButtonGroup } from "@mui/material"

export default function ButtonDeviceRow({
  description,
  selectedValue,
  buttonsValueTitleSet,
  onSelectedChanged,
}) {
  console.log(buttonsValueTitleSet)
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "space-between",
      }}
    >
      <div style={{ flex: 6 }}>
        <p>{description}</p>
        {buttonsValueTitleSet.map((set, index) => <p>{set.title} {set.value}</p>)
        }
      </div>
      {/* <div
        style={{
          display: "flex",
          flex: 1,
          flexDirection: "row",
          justifyContent: "space-between",
        }}
      > */}
        <ToggleButtonGroup
          value={selectedValue}
          exclusive
          onChange={onSelectedChanged}
          aria-label="text alignment"
        >
          {buttonsValueTitleSet.map((set, index) => 
            <ToggleButton value={set.value} aria-label={set.title}>
              <p>{set.title}</p>
              </ToggleButton>
          )}

          {/* <ToggleButton value="left" aria-label="left aligned">
            <FormatAlignLeftIcon />
          </ToggleButton>
          <ToggleButton value="center" aria-label="centered">
            <FormatAlignCenterIcon />
          </ToggleButton>
          <ToggleButton value="right" aria-label="right aligned">
            <FormatAlignRightIcon />
          </ToggleButton>
          <ToggleButton value="justify" aria-label="justified" disabled>
            <FormatAlignJustifyIcon />
          </ToggleButton> */}
        </ToggleButtonGroup>
      {/* </div> */}
    </div>
  );
}
