export default function SensorValueRow({ description, value, measure }) {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        background: 'red',
      }}
    >
      <div style={{ flex: 1, background: 'green', padding: 0 }}>
        <p>{description}</p>
      </div>

      <div
        style={{
          display: "flex",
          flex: 1,
          flexDirection: "row",
          justifyContent: "space-between",
          background: 'blue'
        }}
      >
        <p>{value}</p>
        <p>{measure}</p>
      </div>
    </div>
  );
}
