import { ToggleButton, ToggleButtonGroup } from "@mui/material";

export default function ButtonDeviceRow({
  description,
  selectedValue,
  buttonsValueTitleSet,
  setSelectedValue,
}) {
  const handleAlignment = (event, value) => {
    console.log(value);
    setSelectedValue(value);
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "space-between",
      }}
    >
      <div style={{ flex: 6, background: 'red'}}>
        <p>{description}</p>
      </div>

      <div
        style={{
          flex: 1,
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <ToggleButtonGroup
          value={selectedValue}
          exclusive={true}
          onChange={handleAlignment}
          // aria-label="text alignment"
        >
          {buttonsValueTitleSet.map((set, index) => (
            <ToggleButton value={set.value} 
            // aria-label={set.title}
            >
              <p>{set.title}</p>
            </ToggleButton>
          ))}
        </ToggleButtonGroup>
      </div>
    </div>
  );
}
