import logo from "./logo.svg";
import "./App.css";
import { Box, Container, Divider, Stack } from "@mui/material";
import { sizing } from "@mui/system";
import SensorValueRow from "./components/SensorValueRow";
import { useEffect, useState } from "react";
import ButtonDeviceRow from "./components/ButtonDeviceRow";
import { ON_OF_SET, MEASUREMENTS } from "./util/Constants";
import { getData, updateDeviceState } from "./API/IoT";

function App() {
  const [sensorState, setSensorState] = useState({
    motion1: 0,
    motion2: 0,
    temperature: 0,
    moisture: 0,
  });

  const [deviceState, setDeviceState] = useState({
    lamp1: "off",
    lamp2: "off",
    lamp3: "off",
  });

  useEffect(() => {
    const fetchData = async () => {
      const response = await getSensorData()
      if(response.success){
        setSensorState({...response.response});
      }
    };
    fetchData();
  }, [sensorState]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await getDeviceData()
      if(response.success){
        setDeviceState({...response.response});
      }
    };
    fetchData();
  }, []);

  return (
    <div className="App-header">
      <Container
        maxWidth="lg"
        sx={{ backgroundColor: "#778899", minHeight: "100vh" }}
      >
        <Stack>
          <h2>Sensor Data:</h2>
          <Stack sx={{ marginLeft: 3 }}>
            <SensorValueRow
              description={"Motion (1)"}
              value={sensorState.motion1}
              measure={MEASUREMENTS.distance}
            />
            <SensorValueRow
              description={"Motion (2)"}
              value={sensorState.motion2}
              measure={MEASUREMENTS.distance}
            />
            <SensorValueRow
              description={"Temperature"}
              value={sensorState.temperature}
              measure={MEASUREMENTS.temperature}
            />
            <SensorValueRow
              description={"Moisture"}
              value={sensorState.moisture}
              measure={MEASUREMENTS.moisture}
            />
          </Stack>
          <Divider />

          <h2>Device Status:</h2>
          <Stack sx={{ marginLeft: 3 }}>
            <ButtonDeviceRow
              description={"Light (1)"}
              selectedValue={deviceState.lamp1}
              buttonsValueTitleSet={ON_OF_SET}
              setSelectedValue={(value) => {
                setDeviceState({ ...deviceState, lamp1: value });
                updateDeviceState("lamp1", value);
              }}
            />
            <ButtonDeviceRow
              description={"Light (2)"}
              selectedValue={deviceState.lamp2}
              buttonsValueTitleSet={ON_OF_SET}
              setSelectedValue={(value) => {
                setDeviceState({ ...deviceState, lamp2: value });
                updateDeviceState("lamp2", value);
              }}
            />
            <ButtonDeviceRow
              description={"Light (3)"}
              selectedValue={deviceState.lamp3}
              buttonsValueTitleSet={ON_OF_SET}
              setSelectedValue={(value) => {
                setDeviceState({ ...deviceState, lamp3: value });
                updateDeviceState("lamp3", value);
              }}
            />
          </Stack>
        </Stack>
      </Container>
    </div>
  );
}

export default App;
