export default function SensorValueRow({ description, value, measure }) {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        margin: 0,
        padding: 0,
      }}
    >
      <div style={{ flex: 6, margin: 0, padding: 0 }}>
        <p>{description}</p>
      </div>

      <div
        style={{
          display: "flex",
          flex: 1,
          flexDirection: "row",
          justifyContent: "space-between",
          margin: 0,
          padding: 0,
        }}
      >
        <p>{value}</p>
        <p>{measure}</p>
      </div>
    </div>
  );
}
