import logo from "./logo.svg";
import "./App.css";
import { Box, Container, Divider, Stack } from "@mui/material";
import { sizing } from "@mui/system";
import SensorValueRow from "./components/SensorValueRow";
import { useEffect, useState } from "react";
import ButtonDeviceRow from "./components/ButtonDeviceRow";
import { ON_OF_SET, MEASUREMENTS } from "./util/Constants";
import { getDeviceData, getSensorData, updateDeviceState } from "./API/IoT";

function App() {
  const [sensorState, setSensorState] = useState({
    motion1: {
      description: "Motion (1)",
      value: 0,
      measure: MEASUREMENTS.distance,
    },
    motion2: {
      description: "Motion (2)",
      value: 0,
      measure: MEASUREMENTS.distance,
    },
    temperature: {
      description: "Temperature",
      value: 0,
      measure: MEASUREMENTS.temperature,
    },
    moisture: {
      description: "Moisture",
      value: 0,
      measure: MEASUREMENTS.moisture,
    },
  });

  // const [sensorState, setSensorState] = useState({
  //   motion1: 0,
  //   motion2: 0,
  //   temperature: 0,
  //   moisture: 0,
  // });

  const [deviceState, setDeviceState] = useState({
    light1: "off",
    light2: "off",
    light3: "off",
  });

  useEffect(() => {
    const fetchData = async () => {
      const copy = { ...sensorState };
      const response = await getSensorData();
      if (response.success) {
        Object.keys(response.response).map((key, index) =>
          copy[key].value = response.response[key]
        );
        setSensorState({ ...copy });
      }
    };
    fetchData();
  }, [sensorState]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await getDeviceData();
      if (response.success) {
        setDeviceState({ ...response.response });
      }
    };
    fetchData();
  }, []);

  return (
    <div className="App-header">
      <Container
        maxWidth="lg"
        sx={{ backgroundColor: "#778899", minHeight: "100vh" }}
      >
        <Stack>
          <h2>Sensor Data:</h2>

          <Stack sx={{ marginLeft: 3 }}>
            {Object.keys(sensorState).map((key, index) => (
              <SensorValueRow
                description={sensorState[key].description}
                value={sensorState[key].value}
                measure={sensorState[key].measure}
              />
            ))}
          </Stack>

          {/* <Stack sx={{ marginLeft: 3 }}>
            <SensorValueRow
              description={"Motion (1)"}
              value={sensorState.motion1}
              measure={MEASUREMENTS.distance}
            />
            <SensorValueRow
              description={"Motion (2)"}
              value={sensorState.motion2}
              measure={MEASUREMENTS.distance}
            />
            <SensorValueRow
              description={"Temperature"}
              value={sensorState.temperature}
              measure={MEASUREMENTS.temperature}
            />
            <SensorValueRow
              description={"Moisture"}
              value={sensorState.moisture}
              measure={MEASUREMENTS.moisture}
            />
          </Stack> */}
          <Divider />

          <h2>Device Status:</h2>
          <Stack sx={{ marginLeft: 3 }}>
            <ButtonDeviceRow
              description={"Light (1)"}
              selectedValue={deviceState.light1}
              buttonsValueTitleSet={ON_OF_SET}
              setSelectedValue={(value) => {
                setDeviceState({ ...deviceState, light1: value });
                updateDeviceState("light1", value);
              }}
            />
            <ButtonDeviceRow
              description={"Light (2)"}
              selectedValue={deviceState.light2}
              buttonsValueTitleSet={ON_OF_SET}
              setSelectedValue={(value) => {
                setDeviceState({ ...deviceState, light2: value });
                updateDeviceState("light2", value);
              }}
            />
            <ButtonDeviceRow
              description={"Light (3)"}
              selectedValue={deviceState.light3}
              buttonsValueTitleSet={ON_OF_SET}
              setSelectedValue={(value) => {
                setDeviceState({ ...deviceState, light3: value });
                updateDeviceState("light3", value);
              }}
            />
          </Stack>
        </Stack>
      </Container>
    </div>
  );
}

export default App;
