import logo from "./logo.svg";
import "./App.css";
import { Box, Container, Divider, Stack } from "@mui/material";
import { sizing } from "@mui/system";
import SensorValueRow from "./components/SensorValueRow";
import { useState } from "react";
import ButtonDeviceRow from "./components/ButtonDeviceRow";

function App() {
  const distanceMeasure = "mm";
  const temperatureMeasure = "Celcius";
  const [sensorState, setSensorState] = useState({
    mtS1: 0,
    mtS2: 0,
    tmpS: 0,
  });

  const [deviceState, setDeviceState] = useState({
    lamp1: "off",
    lamp2: "off",
    lamp3: "off",
  });

  const lampStates = [
    { value: "on", title: "On" },
    { value: "off", title: "Off" },
  ];

  return (
    <div className="App-header">
      <Container
        maxWidth="lg"
        sx={{ backgroundColor: "#778899", minHeight: "100vh" }}
      >
        <Stack>
          <h4>Sensor Data:</h4>
          <Stack sx={{marginLeft: 3}}>
            <SensorValueRow
              description={"Motion sensor (1)"}
              value={sensorState.mtS1}
              measure={distanceMeasure}
            />
            <SensorValueRow
              description={"Motion sensor (2)"}
              value={sensorState.mtS2}
              measure={distanceMeasure}
            />
            <SensorValueRow
              description={"Temperature sensor"}
              value={sensorState.tmpS}
              measure={temperatureMeasure}
            />
          </Stack>
        <Divider />

        <h2>Device Status:</h2>
        <Stack sx={{marginLeft: 3}}>
            <ButtonDeviceRow
              description={"Light (1)"}
              selectedValue={deviceState.lamp1}
              buttonsValueTitleSet={lampStates}
              setSelectedValue={(value) =>
                setDeviceState({ ...deviceState, lamp1: value })
              }
            />
            <ButtonDeviceRow
              description={"Light (2)"}
              selectedValue={deviceState.lamp2}
              buttonsValueTitleSet={lampStates}
              setSelectedValue={(value) =>
                setDeviceState({ ...deviceState, lamp2: value })
              }
            />
            <ButtonDeviceRow
              description={"Light (3)"}
              selectedValue={deviceState.lamp3}
              buttonsValueTitleSet={lampStates}
              setSelectedValue={(value) =>
                setDeviceState({ ...deviceState, lamp2: value })
              }
            />          
        </Stack>
        </Stack>


{/* 
        <div style={{ display: "flex", flexDirection: "row" }}>
          <div style={{ flex: 1 }} />
          <div style={{ flex: 15, flexDirection: "column" }}>

          </div>
        </div> */}
      </Container>
    </div>
  );
}

export default App;
